import React from 'react'
import "./Login.css"
import {Link} from "react-router-dom";

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const signIn = e => {
        e.preventDefault();
        // firebase login
    }

    const register = e =>{}
        e.preventDefault();
        // firebase register}
    return (
        
        <div className="login">
            <Link to="/">
            <img className="login_logo" src='http://media.corporate-ir.net/media_files/IROL/17/176060/Oct18/Amazon%20logo.PNG' />
            </Link>

            <div className="login_container">
                <h1>Sign-in</h1>

                <form>
                    <h5>E-mail</h5>
                    <input type="text" value={email} onChange =
                    {e => setEmail(e.target.value)}/>

                    <h5>Password</h5>
                    <input type="password" value={password} onChange =
                    {e => setPassword(e.target.value)}/>

                    <button onClick={register} type="submit" onClick={signIn} className="login_signInButton">Sign in</button>
                </form>

                <p>
                    By signing-in you agree to the AMAZON FAKE CLONE Conditions of Use & Sale. Please
                    see our Privacy Notice, our Cookies Notice and our Interest-Based Ads Notice.
                </p>
                <button className="login_registerButton">Create your Amazon Account</button>

            </div>
        </div>
        
    )

}

export default Login
